mapper.insert:
        INSERT /* com.onedu.mvc.member.model.dao.MemberDAO#insertMember() */
          INTO MEMBER 
        (
          USER_ID
		, USER_PWD
		, USER_NAME
		, EMAIL
		, PHONE
		, GENDER
		, BIRTHDAY
		, POSTAL  
		, ADDRESS
		, ADDRESS2

        )
        VALUES 
        (
          #{ userId }
        , #{ userPwd }
        , #{ userName }
        , #{ email }
        , #{ phone }
        , #{ gender }        
        , #{ birthday }
        , #{ postal }
        , #{ address }
        , #{ address2 }

        )

mapper.select:
SELECT /* com.onedu.mvc.member.model.dao.MemberDAO#selectFindId() */
	   USER_ID
  FROM MEMBER
 WHERE PHONE = #{ phone }
   AND EMAIL = #{ email }

mapper.update:
UPDATE /* com.onedu.mvc.member.model.dao.MemberDAO#updateMember() */
       MEMBER
   SET USER_NAME = #{ userName }
     , PHONE = #{ phone }
     , EMAIL = #{ email }
     , POSTAL = #{ postal }
     , ADDRESS = #{ address }
     , ADDRESS2 = #{ address2 }
 WHERE USER_ACT = '활동회원'
   AND USER_ID = #{ userId }

UPDATE /* com.onedu.mvc.member.model.dao.MemberDAO#updateMemberPassword() */
       MEMBER
   SET USER_PWD = #{ userPwd }
 WHERE USER_ACT = '활동회원'
   AND USER_ID = #{ id }
